import { Component, HostBinding, Input } from '@angular/core';
import { BadgeNumberColor } from '@enums/ui.enum';

@Component({
  selector: 'app-badge-number, [app-badge-number]',
  imports: [],
  templateUrl: './badge-number.component.html',
  styleUrl: './badge-number.component.scss'
})
export class BadgeNumberComponent {
  @Input() color: BadgeNumberColor = BadgeNumberColor.Primary;

  @HostBinding('class') get hostClass() {
    return this.initClass();
  }

  initClass() {
    let classs = ['app-badge-number'];
    classs.push('app-badge-number-' + this.color);
    return classs.join(' ');
  }
}

:host {
  @apply min-w-5 h-5 flex items-center justify-center text-center text-label-small rounded-full ring-[1.5px] ring-border-borderwhite;
  &.app-badge-number {
    &-primary {
      @apply bg-background-backgroundbrandprimary text-text-textoncolorprimary;
    }
    &-secondary {
      @apply bg-brand-brandprimary20 text-text-textlink;
    }
  }
}

::ng-deep {
  .ant-tabs-tab:not(.ant-tabs-tab-active) {
    .app-badge-number-primary {
      @apply bg-brand-brandprimary20 text-text-textlink;
    }
  }
}
