import { CommonModule } from '@angular/common';
import { Component, HostBinding, Input, TemplateRef } from '@angular/core';
import { CardEmptyIconType, CardEmptyType } from '@enums/ui.enum';

@Component({
  selector: 'app-card-empty, [app-card-empty]',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './card-empty.component.html',
  styleUrl: './card-empty.component.scss',
})
export class CardEmptyComponent {
  @Input() type: CardEmptyType = CardEmptyType.List;
  @Input() title!: string;
  @Input() moreContent!: TemplateRef<void>;

  @HostBinding('class.app-card-empty') card = true;

  mediaSrc!: string;

  ngOnInit() {
    switch (this.type) {
      case CardEmptyType.User:
        this.mediaSrc = CardEmptyIconType.User;
        break;
      default:
        this.mediaSrc = CardEmptyIconType.List;
        break;
    }
  }
}

:host {
  &.app-card-empty {
    @apply block pb-3;
    .card-empty-inner {
      @apply space-y-3 w-full mx-auto py-6;
    }

    .empty-media {
      @apply w-[120px] h-[120px] mx-auto;
    }

    .content {
      @apply text-text-textneutralsecondary text-body-medium-regular text-center;
    }

    .title {
      @apply text-text-textneutralprimary text-body-large-semibold mb-1;
    }

    .descriptions {
      // @apply text-text-secondary text-body-large-medium;
    }
  }
}
