import { CommonModule } from '@angular/common';
import { Component, HostBinding, Input } from '@angular/core';
import { PageWrapperSize } from '@enums/ui.enum';

@Component({
  selector: 'app-pagewrapper',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './page-wrapper.component.html',
  styleUrls: ['./page-wrapper.component.scss'],
})
export class PageWrapperComponent {
  @Input() size: PageWrapperSize = PageWrapperSize.Medium;
  @Input() padding: boolean = true;

  @HostBinding('class') get hostClass() {
    return this.initClass();
  }

  initClass() {
    let classs = ['app-page-wrapper'];
    classs.push('app-page-wrapper-' + this.size);
    if (!this.padding) classs.push('no-padding');
    return classs.join(' ');
  }
}

// name, size
$pageSizes: ("medium", var(--page-wrapper)), ("large", var(--page-wrapper-lg)),
  ("xlarge", var(--page-wrapper-xl)), ("full", var(--page-wrapper-full));
:host {
  &.app-page-wrapper {
    @apply block mx-auto;

    &:not(.no-padding) {
      @apply px-[var(--pd-base)];
    }
  }

  @each $name, $pgSize in $pageSizes {
    &.app-page-wrapper-#{$name} {
      --pageWraperSizeCurrent: #{$pgSize};
      max-width: calc($pgSize + var(--pd-base) * 2);
    }
  }
}
