import { CommonModule } from '@angular/common';
import {
  Component,
  EventEmitter,
  HostBinding,
  Input,
  Output,
  TemplateRef,
} from '@angular/core';
import { TabSize, TabTheme, TabType } from '@enums/ui.enum';
import { TranslateModule } from '@ngx-translate/core';
import { NzTabsModule } from 'ng-zorro-antd/tabs';
@Component({
  selector: 'app-tab',
  standalone: true,
  imports: [CommonModule, NzTabsModule, TranslateModule],
  templateUrl: './tab.component.html',
  styleUrls: ['./tab.component.scss'],
})
export class TabComponent {
  @Input() size: TabSize = TabSize.Md;
  @Input() type: TabType = TabType.Pill;
  @Input() fill: boolean = false;
  @Input() tabCenter: boolean = false;
  @Input() theme: TabTheme = TabTheme.Brand;
  @Input() initIndex: number = 0;
  @Input() tabs!: any[];
  @Input() tabTitleTpl!: TemplateRef<any>;
  @Output() tabChangeIndex = new EventEmitter<number>();

  @HostBinding('class') get hostClass() {
    return this.initClass();
  }

  initClass() {
    let classs = ['app-tab', 'app-tab-' + this.type];
    if (this.fill) classs.push('app-tab-fill');
    if (this.size !== TabSize.Md) classs.push('app-tab-' + this.size);
    if (this.theme !== TabTheme.Default) classs.push('app-tab-' + this.theme);
    return classs.join(' ');
  }

  tabChange(index: number) {
    this.tabChangeIndex.emit(index);
    this.initIndex = index;
  }
}

@if (tabs) {
  <nz-tabset
    [nzSelectedIndex]="initIndex"
    (nzSelectedIndexChange)="tabChange($event)"
    [nzCentered]="tabCenter"
  >
    @for (tab of tabs; track tab; let i = $index) {
      <nz-tab [nzTitle]="titleTpl">
        <ng-template #titleTpl>
          <div *ngIf="tabTitleTpl; else titleTplDefault" class=" flex items-center gap-2">
            <ng-container
              [ngTemplateOutlet]="tabTitleTpl"
              [ngTemplateOutletContext]="{
                tab: tab,
                tabIndex: i,
                selectedIndex: initIndex,
              }"
            />
          </div>
          <ng-template #titleTplDefault>
            {{ tab | translate }}
          </ng-template>
        </ng-template>
      </nz-tab>
    }
  </nz-tabset>
}

:host {
  ::ng-deep {
    .ant-tabs {
      &-nav {
        margin: 0 !important;

        &::before {
          border-bottom: 0 !important;
        }

        &-wrap {
          @apply w-full flex;

          &:before,
          &:after {
            display: none;
          }
        }
      }

      &-tab {
        @apply p-0 m-0 flex items-center justify-center w-full h-10;
        @apply border-b-transparent #{!important};

        & + & {
          margin: 0;
        }

        &-btn {
          @apply px-4 md:px-6 py-[10px] transition-none text-label-medium text-text-textneutralsecondary;
          &,
          &:focus {
            // @apply text-text-disable;
          }
        }
      }

      &-nav-operations {
        .ant-tabs-nav-more {
          @apply py-0;
          @apply px-2 #{!important};
        }
      }
    }
  }

  &.app-tab-line,
  &.app-tab-line-fill {
    ::ng-deep .ant-tabs-tab {
      height: var(--height-tabline-md);
    }
  }

  &.app-tab {
    @apply block;
  }

  &.app-tab-sm {
    ::ng-deep .ant-tabs-tab {
      height: var(--height-input-sm);
      &-btn {
        @apply py-[6px];
      }
    }

    &.tab-line,
    &.tab-line-fill {
      ::ng-deep .ant-tabs-tab {
        height: var(--height-tabline-sm);
      }
    }
  }

  &.app-tab-lg {
    ::ng-deep .ant-tabs-tab {
      height: var(--height-input-lg);
      &-btn {
        @apply py-[6px];
      }
    }

    &.tab-line,
    &.tab-line-fill {
      ::ng-deep .ant-tabs-tab {
        height: var(--height-tabline-lg);
      }
    }
  }

  &.app-tab-fill {
    ::ng-deep {
      .ant-tabs-nav-list {
        @apply flex-1;
      }
    }
  }

  &.app-tab-line {
    ::ng-deep {
      .ant-tabs-ink-bar::before {
        width: 20px;
      }
    }
  }

  &.app-tab-line,
  &.app-tab-line-fill {
    ::ng-deep {
      .ant-tabs {
        &-ink-bar {
          @apply bg-transparent;
          @apply h-[2px] #{!important};

          &::before {
            content: "";
            @apply h-full block rounded-full mx-auto;
          }
        }

        &-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
          // @apply text-primary;
        }
      }
    }
  }

  &.app-tab-line-fill {
    ::ng-deep {
      .ant-tabs-ink-bar::before {
        width: 100%;
      }
    }
  }

  &.app-tab-pill {
    @apply rounded-full;

    ::ng-deep {
      .ant-tabs {
        &-nav {
          @apply p-1 bg-neutral-black5 rounded-full;

          &-wrap {
            @apply rounded-full;
            .ant-tabs-ink-bar {
              @apply rounded-full z-[-1] shadow-shadow-xs bg-background-backgroundwhite;
              @apply h-full #{!important};
            }
          }
        }

        &-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
          // @apply text-primary;
        }

        &-ink-bar {
          // @apply bg-bg-base;
        }
      }
      .ant-tabs-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
        @apply text-text-textlink
      }
    }

    &.app-tab-brand {
      ::ng-deep .ant-tabs {
        &-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
          // @apply text-text-base;
        }

        &-ink-bar {
          // @apply bg-primary;
        }
      }
    }
  }
  &.app-tab-button-pill {
    @apply rounded-full;

    ::ng-deep {
      .ant-tabs {
        &-nav {
          &-wrap {
            @apply rounded-full;
          }
        }

        &-ink-bar {
          @apply hidden;
        }
      }
      .ant-tabs-nav-list {
        @apply gap-2;
      }
      .ant-tabs-tab {
        .ant-tabs-tab-btn {
          @apply bg-background-backgroundsecondary  border border-background-backgroundsecondary text-text-textneutralsecondary border-solid rounded-full py-2 px-3 h-full;
        }

        &.ant-tabs-tab-active {
          .ant-tabs-tab-btn {
            @apply text-text-textlink bg-brand-brandprimary10 border-border-borderbrand;
          }
        }
      }
    }
  }
}
