import { CommonModule } from '@angular/common';
import { Component, EventEmitter, forwardRef, HostBinding, Input, Output, TemplateRef } from '@angular/core';
import { NG_VALUE_ACCESSOR, ReactiveFormsModule } from '@angular/forms';
import { BaseCVADirective } from '@directives/base-cva/base-cva.directive';
import { NzSwitchModule } from 'ng-zorro-antd/switch';

@Component({
  selector: 'app-toggle',
  imports: [CommonModule, NzSwitchModule, ReactiveFormsModule],
  templateUrl: './toggle.component.html',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => ToggleComponent),
      multi: true,
    },
  ],
  styleUrl: './toggle.component.scss'
})
export class ToggleComponent<T>
  extends BaseCVADirective<T> {
  @HostBinding('class.app-switch') switch = true;
  @Input() label?: string;
  @Input() nzControl: boolean = false;
  @Input() labelLeftTpl?: TemplateRef<void> | null;

  @Output() clickEvent = new EventEmitter<void>();

  handleClick() {
    this.clickEvent.emit();
  }

  handleLabelClick() {
    if (!this.control.disabled) {
      this.control.setValue(!this.control.value);
      this.clickEvent.emit();
    }
  }
}

:host {
  .switch-inner {
    @apply flex items-center gap-2 text-label-large;
  }

  .switch-label-wrap,
  .switch-label {
    @apply cursor-pointer select-none;
  }

  .switch-disabled .switch-label-wrap,
  .switch-disabled .switch-label {
    @apply cursor-not-allowed opacity-50;
  }
}

::ng-deep {
  nz-switch {
    @apply flex;
  }
  .ant-switch {
    @apply min-w-10 h-6 leading-6 bg-neutral-gray40;
  }
  .ant-switch-handle {
    @apply size-5;
    &::before {
      @apply shadow-elevation-1;
    }
  }
  .ant-switch-checked:focus {
    @apply shadow-focus-ring;
  }
  .ant-switch-checked {
    @apply bg-icon-iconbrandprimary;
  }
  .ant-switch-checked .ant-switch-handle {
    left: calc(100% - 20px - 2px);
  }
}
